// Load Web3 and wallet manager
document.write('<script src="https://cdn.jsdelivr.net/npm/web3@1.5.2/dist/web3.min.js"></script>');
document.write('<script src="js/wallet-config.js"></script>');
document.write('<script src="js/wallet-manager.js"></script>');

// Initialize wallet connection handlers
document.addEventListener('DOMContentLoaded', () => {
    // Connect wallet button handler
    const connectButtons = document.querySelectorAll('.wallet-connect');
    connectButtons.forEach(button => {
        button.addEventListener('click', async () => {
            await window.walletManager.connectWallet();
        });
    });

    // Disconnect wallet button handler
    const disconnectButtons = document.querySelectorAll('.wallet-disconnect');
    disconnectButtons.forEach(button => {
        button.addEventListener('click', async () => {
            await window.walletManager.disconnectWallet();
        });
    });

    // Initialize tooltips if available
    if (typeof bootstrap !== 'undefined' && bootstrap.Tooltip) {
        const tooltips = document.querySelectorAll('[data-bs-toggle="tooltip"]');
        tooltips.forEach(tooltip => new bootstrap.Tooltip(tooltip));
    }
}); 